export const WaveProgress = ({progress = 70, ...props}) => (
  <Svg
    width={'100%'}
    height={40}
    viewBox="0 0 200 31"
    preserveAspectRatio="none"
    fill="none"
    {...props}>
    <G>
      {Array(50)
        .fill('')
        .map((_, index) => {
          const x = 4 * index;
          const height = 40 - Math.floor(Math.random() * 40);
          const y = (31 - height) / 2;
          const fillOpacity = index * 2 <= progress ? 1 : 0.1;
          return (
            <Rect
              width={2}
              x={x}
              y={y}
              rx={4}
              height={height}
              fill={props.color || 'white'}
              fillOpacity={fillOpacity}
              key={`wave_progress_${generateComponentKey()}`}
            />
          );
        })}
    </G>
  </Svg>
);

